import React, { useState } from "react";
import PersonalDetails from './components/PersonalDetails';
import BussinessDetails from './components/BussinessDetails';
import LoanApplicationDetails from './components/LoanApplicationDetails'

const Form = () => {
    const [activeTab , setActiveTab] = useState(0)

    const tabDisplay = () =>{
        switch(activeTab){
            case 0 :
                return <PersonalDetails />
            case 1 :
                return <BussinessDetails />
            case 2 :
                return <LoanApplicationDetails />
            default:
                return
        }
    }
  return (
    <div className="form-container">
        <h2>Loan Application</h2>
        <ul className="nav nav-tabs">
            <li className={activeTab === 0 ? 'active nav-item nav-link' : 'nav-item nav-link'}
                onClick={()=> setActiveTab(0)}>
                Step 1
            </li>
            <li className={activeTab === 1 ? 'active nav-item nav-link' : 'nav-item nav-link'}
                onClick={()=> setActiveTab(1)}>
                Step 2
            </li>
            <li className={activeTab === 2 ? 'active nav-item nav-link' : 'nav-item nav-link'}
                onClick={()=> setActiveTab(2)}>
                Step 3
            </li>
        </ul>
        <div className="form-output">
            {tabDisplay()}
        </div>
    </div>
  )
}

export default Form
